INSERT INTO `product_1_18_182_1821` (`id`, `id_catalog`, `name`, `producer`, `model`, `existence`, `weight`, `age`, `description`, `for`, `price`) VALUES
(1, 1821, 'Расческа для собак для вычесывания блох', 'Hartz', '94803', 1, '', '', 'Расчёска позволяет тщательно вычёсывать частички пыли и грязи с шерсти животного, не травмируя поверхность кожи и не повреждая структуру шерсти. Рекомендуется для каждодневного ухода за шерстью собак. Изготовлено в США.', '', '54000'),
(2, 1821, 'Hartz Расческа для собак с длинными металлическими зубьями', 'Hartz', '83802\n', 1, '', '', 'Щётка предназначена для тщательного ухода за шерстью. Она легко удаляет частички пыли и грязи с шерсти животного, не повреждая поверхность кожи и не травмируя структуру шерсти. Рекомендуется для каждодневного ухода за шерстью. Изготовлено в США. \r\n', '', '58000'),
(3, 1821, 'Расчёска для собак с облитыми кончиками', 'Hartz', '89098\r\n', 1, '', '', 'Используется для собак любых пород (пудели, бишоны, керри-блю-терьеры, сеттеры, спаниели, скотчи, таксы, шнауцеры, весты, эрдели, йорки, кроме очень короткошерстных собак.Хорошо поднимает шерсть у длинно и гладкошерстных собак после использования пуход', '', '62000'),
(4, 1821, 'Набор для стрижки и полировки когтей', 'Hartz', '85771\r\n\r\n', 0, '', '', 'Набор для стрижки и полировки когтей у собак Hartz\r\n\r\nНабор для стрижки и полировки когтей Hartz с успехом используют владельцы собак любых пород и размеров. Изготовленный из легкой, надежной, особо прочной стали высокого качества, секатор мгновенно и безболезненно удалит отросшие когти вашего любимца. Животное не почувствует ни малейшего дискомфорта. А с помощью удобной пилочки обрезанные края закругляются и сглаживаются, она же позволяет удалить острые заусенцы, которыми собака может поцарапать хозяина или предметы в доме.\r\n\r\nПреимущества данного товара:\r\n\r\nЛезвия секатора сделаны из особо прочной нержавеющей стали и имеют исключительно острый режущий край. Крепежное соединение секатора дополнительно усиленно. Остриженные с помощью секатора Hartz когти не слоятся и не ломаются. А пилочка помогает завершить педикюр вашего питомца, округляя концы и убирая мелкие шероховатости, оставшиеся после стрижки.\r\n\r\nСпособ применения:\r\n\r\nПри первом применении секатора мы рекомендуем его продезинфицировать. Очень важно, чтобы животное было надежно зафиксировано в момент проведения процедуры. Это требование вашей собственной безопасности и безопасности вашего питомца. Секатор имеет чрезвычайно острые, заточенные лезвия. Неосторожное обращение с ними чревато серьезной угрозой для здоровья. Когда животное обездвижено, аккуратно нажмите на подушечку лапы, чтобы выдвинуть коготь.', '', '0'),
(5, 1821, 'Расческа-грабли для собак', 'Hartz', '98118', 1, '', '', 'Расческа-грабли для собак с длинной или густой шерстью. Легко удаляет отмершиеи волосы подшерстка во время линьки. С прорезиненной ручкой.', '', '73000'),
(6, 1821, 'Щетка-пуходерка для собак', 'Hartz', '83799', 1, '', '', 'Пуходерка имеет удобную пластмассовую ручку, шетка сделана из прочного металла. Хорошо расчесывает спутанную шерсть, вычесывает колтуны', '', '85000'),
(7, 1821, 'Щетка двухсторонняя, щетиновая и массажная для собак', 'Hartz', '83801', 1, '', '', '', '', '73000'),
(8, 1821, 'Щетка для короткошерстных собак, резиновые шишечки', 'Hartz', '12219', 1, '', '', '', '', '73000');

