INSERT INTO `product_3_34_344` (`id`, `id_catalog`, `name`, `producer`, `model`, `existence`, `weight`, `age`, `description`, `for`, `price`) VALUES
(1, 344, 'Шампунь Hartz от блох и клещей', 'Hartz', '', 1, '350 мл', 'от 12 недель', 'Шампунь инсекто-акарицидный «Хартц - Контрол» для защиты котят и кошек от блох, вшей и иксодовых клещей. Убивает блох, клещей и вшей, находящихся на животном. Мягко очищает шерсть и кожу от загрязнений. Благодаря протеиновому кондиционеру, после мытья, шерсть не сваливается, легко расчесывается и приобретает приятный хвойный аромат. \r\nПрименение: \r\nОбильно смочить кожно-волосяной покров теплой водой, равномерно массажными движениями распределить небольшое количество шампуня по всей поверхности тела. Оставить шампунь на 5 минут, затем смыть водой и подсушить полотенцем. Если необходимо, процедуру можно повторить через 1 неделю. \r\nВнимание! \r\nМожно использовать для котят не ранее 12-ти недельного возраста. \r\nНе рекомендуется применять шампунь для больных, ослабленных животных, беременных и кормящих самок, без консультации с ветеринаром. \r\nХранить шампунь в заводской упаковке при комнатной температуре, в недоступном для детей и животных месте, отдельно от пищевых продуктов и кормов для животных. \r\nСостав: \r\nАктивные ингредиенты: d-транс Аллетрин - 0,109%; N-октил бициклогептен дикарбоксимид - 0,151%; другие ингредиенты - 99,740%.', 'для кошек от 12 недель', '63000'),
(2, 344, 'Шампунь Hartz с контролем вывода шерсти', 'Hartz', '', 1, '443 мл', '', 'Шампунь Хартц для кошек с контролем вывода шерсти, 443 мл. Специально разработан для кошек и котят, способствует удалению ненужных волосков с шерсти животного, тем самым защищает кошку от возникновения комков шерсти в желудке. Данный шампунь делает шерсть животного шелковистой, блестящей, облегчает расчесывание и препятствует возникновению колтунов.', 'для кошек', '70000'),
(3, 344, 'Гипоаллергенный шампунь Hartz', 'Hartz', '', 0, '213 мл', '', 'Гипоаллергенный шампунь Hartz для собак и кошек без ароматизаторов и красителей (213 мл.)\r\n\r\nНежный, эффективный, экологически безопасный шампунь для чувствительной кожи.\r\n\r\nПрименение: нанести небольшое количество шампуня на мокрую шерсть, тщательно взбить пену, оставить на некоторое время для усиления эффекта, смыть водой.', 'для собак и кошек', ''),
(4, 344, 'Шампунь Hartz для мытья кошек без использования воды', 'Hartz', '', 1, '236 мл', '', 'Шампунь для кошек для мытья без воды - отлично удаляет грязь и дезодорирует шерсть.\r\n\r\nСпособ применения:\r\nМожно использовать как на сухую так и мокрую шерсть. Хорошо увлажните шерсть, избегая попадания в глаза и уши. Далее расчесывайте шерсть для равномерного увлажнения, после промокните шерсть сухой салфеткой.\r\nНе требует смывания, подходит для ежедневного использования.', 'для кошек', '79000');

